<!DOCTYPE html><html lang="en"><head>  <title>Bootstrap Example</title>  <meta charset="utf-8">  <meta name="viewport" content="width=device-width, initial-scale=1">  <link rel="stylesheet" href="https://maxcdn.bootstrapcdn.com/bootstrap/4.3.1/css/bootstrap.min.css">  <script src="https://ajax.googleapis.com/ajax/libs/jquery/3.4.1/jquery.min.js"></script>  <script src="https://cdnjs.cloudflare.com/ajax/libs/popper.js/1.14.7/umd/popper.min.js"></script>  <script src="https://maxcdn.bootstrapcdn.com/bootstrap/4.3.1/js/bootstrap.min.js"></script>  <style>  /* Make the image fully responsive */  .carousel-inner img {	height: 100vh !important;    width: 100% !important;    object-fit: fill;  }  </style></head><?php error_reporting(0);?><body style="background: #f7f7f7;"><div id="demo" class="carousel slide" data-ride="carousel">	<div class="carousel-inner">		<?php 		$i = 1;		foreach($result as $row) { ?>		<div class="carousel-item <?php if($i==1){ ?> active <?php } ?>">			<?php			$compname="";			if($row->funtype=="1"){				$row1 =  $this->db->query("select id from tbl_medicine where i_code='$row->itemid'")->row();				$row->itemid = $row1->id; 							}			if($row->funtype=="2"){				$row->itemid = $row->compid; 				$row1 =  $this->db->query("select company_full_name from tbl_medicine where compcode='$row->itemid'")->row();				$compname = ucwords(strtolower($row1->company_full_name));			} ?>			<a href="javascript:callandroidfun('<?= $row->funtype; ?>','<?= $row->itemid; ?>','<?= $compname?>','<?= base_url(); ?>uploads/manage_slider2/photo/resize/<?= $row->image; ?>','<?= $row->division; ?>');">				<img src="<?= base_url(); ?>uploads/manage_slider2/photo/resize/<?= $row->image; ?>" data-u="image" class="img_css_forslider"/>			</a>		</div>		<?php 		$i++;		} ?>	</div>	<a class="carousel-control-prev" href="#demo" data-slide="prev">		<span class="carousel-control-prev-icon"></span>	</a>	<a class="carousel-control-next" href="#demo" data-slide="next">		<span class="carousel-control-next-icon"></span>	</a></div><script>function callandroidfun(funtype,id,compname,image,division) {		if(funtype=="1"){		android.fun_Get_single_medicine_info(id);	}	if(funtype=="2"){		android.fun_Featured_brand_medicine_division(id,compname,image,division);	}}</script>